sys_variable_value.value:
(function execute(inputs, outputs) {
    if (inputs.status_code == 200) {
        var response = JSON.parse(inputs.response_body);

        outputs.city = response.city;
        outputs.region = response.region;
        outputs.country = response.country;
        outputs.ip = response.ip;
        outputs.org = response.org;
    } else {
        gs.info("API call failed. Status code: " + inputs.status_code);
    }
})(inputs, outputs);

